import "core-js/modules/es.array.push.js";
import * as echarts from 'echarts';
import { getExamineHistory } from '@/request/testApi';
import { getTime } from '@/utils/utils';
export default {
  mounted() {
    getExamineHistory().then(res => {
      this.historyData = res.data.slice(0, this.itemNumber);
      this.getCharts();
    });
  },
  data() {
    return {
      historyData: [],
      itemNumber: 7
    };
  },
  computed: {
    chartData() {
      const data = [];
      this.historyData.forEach(d => {
        data.push(Number((100 * Number(d.result)).toFixed(1)));
      });
      return data;
    },
    chartDateData() {
      const data = [];
      this.historyData.forEach(d => {
        data.push(getTime(d.timeStamp));
      });
      return data;
    }
  },
  methods: {
    getCharts() {
      const chart = this.$refs.chart;
      const myChart = echarts.init(chart);
      myChart.setOption({
        width: 'auto',
        xAxis: {
          type: 'category',
          data: this.chartDateData
        },
        yAxis: {
          type: 'value'
        },
        series: [{
          data: this.chartData,
          type: 'bar',
          showBackground: true,
          backgroundStyle: {
            color: 'rgba(180, 180, 180, 0.2)'
          }
        }],
        tooltip: {
          trigger: 'axis',
          axisPointer: {
            type: 'shadow'
          },
          formatter: function (params) {
            let data = params[0];
            return data.axisValue + '<br/>' + data.data;
          }
        }
      });
      myChart.on('click', event => {
        console.log(event);
      });
      myChart.on('mouseover', event => {
        console.log(event);
      });
    }
  },
  components: {}
};
